  `sale` enum('off','on') NOT NULL,
  `freeship` enum('off','on') NOT NULL,
  `modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  PRIMARY KEY (`product`),
  KEY `bestselling` (`sold`,`product`),
  KEY `featured` (`featured`,`product`),
